      console.log(`STATUS CODE: ${res.statusCode}`);

      res.on('data', function(data) {

        process.stdout.write(data);
      });

    });

    clientPOSTReq.write(json_payload);
    clientPOSTReq.end();

    clientPOSTReq.on('error', function(err) {
      console.log('ERROR:' + err);
    });
  }
}


/* ===== Websockets Handlers ===== */
function handleWS_OnConnectFailed(error) {
  console.log(error);
}

function handleWS_onConnect(connection) {
  console.log("WS Client connected.");

  // register a message, close, and error handler with the WS client object
  this.client.on('error', handleWS_onMessage(error));
  this.client.on('message', handleWS_onMessage(message));
  this.client.on('close', handleWS_onClose());
}

function handleWS_onClose() {
