const http = require('http');

class Client {


  // This function sends a generic HTTP get response to my local test server
  sendGETRequest() {

    const options = {
      hostname: 'localhost',
      port: 8080,
      path: '/',
      method: 'GET'
    };

    const clientGETReq = http.request(options, function(res) {

      console.log(`STATUS CODE: ${res.statusCode}`);

    });

    clientGETReq.on('error', function(err) {
      console.log('ERROR:' + err);
    });

    clientGETReq.end();

  }

  sendPOSTRequest(json_payload) {

    const options = {
      hostname: 'localhost',
      port: 8080,
      path: '/',
      method: 'POST',

    };

    const clientPOSTReq = http.request(options, function(res) {

      console.log(`STATUS CODE: ${res.statusCode}`);

      res.on('data', function(data) {

        process.stdout.write(data);
      });

    });

    clientPOSTReq.write(json_payload);
    clientPOSTReq.end();

    clientPOSTReq.on('error', function(err) {
      console.log('ERROR:' + err);
    });
  }
}

export default Client;
