import React from 'react';

class Searchables extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      searchable1: "null",
      searchable2: "null",
      searchable3: "null",
      searchable4: "null"
    };
  }

  render() {

    this.updateSearchables();

    return (
      <div>
        <button>{this.state.searchable1}</button>
        <button>{this.state.searchable2}</button>
        <button>{this.state.searchable3}</button>
        <button>{this.state.searchable4}</button>
      </div>
    );

  }

  updateSearchables() {

    switch(this.props.gameState.location) {

      case "Grand Foyer":
        this.setState({
          searchable1: "constellation table",
          searchable2: "telescope",
          searchable3: "astronomy book pile",
          searchable4: "mechanical lever box"
        });
      break;


    }
  }

}

export default Searchables;
