import React from 'react';
const uniqid = require('uniqid'); // use this to generate unique client ID on the client side

class StartScreen extends React.Component {

  constructor(props) {
    super(props);
  }

  render() {

      return (
        <div>
          <h1>Ghoul Hunters</h1>
          <form action="http://localhost:8080/create_room" method="GET">
            <input type="submit" value="Host a Game" />
          </form>
          <form action="http://localhost:8080/join_room" method="GET">
            <input type="text" id="room_id"/>
            <input type="submit" value="Join a Game"/>
          </form>
        </div>
      );
  }

  // I will use pure JS Ajax to submit these forms. I don't want the page to change
  ajaxSubmitGETToCreateRoom() {

    let xhr = new XMLHttpRequest();

    //xhr.open("GET", "http://localhost:8080/create_room?clientID=" + this.props.gameState.clientID, true);
    xhr.open("GET", "webcode.me", true);


    xhr.onreadystatechange = function() {

      // check whether request has been complete
      if(xhr.readyState === XMLHttpRequest.DONE) {
        //check whether the request was successful
        if(xhr.status === 200) {
          console.log(xhr.responseText);
        }
      }
    }


    console.log(this.props.gameState.clientID);
    xhr.send(null);
  }

  ajaxSubmitGETToJoinRoom() {

    //let http = new XMLHttpRequest();
    //xhr.open("GET", "http://localhost:8080/create_room", true)
  }
}

export default StartScreen;
