import React from 'react';

class RoomScreen extends React.Component {

  constructor(props) {
    super(props);
    this.similarClientListItems = [];
    this.renderSimilarClients();
    this.similarClientIndexArray = this.getSimilarClientIndexArray();
  }

  render() {
    return (
      <div>
        <h1>Room Code: {this.props.gameState.roomID}</h1>
        <h2>Players:</h2>
        <ul>
          <li>You: {this.props.gameState.clientID}</li>
          {this.similarClientListItems}
        </ul>
      </div>
    )
  }

  renderSimilarClients() {

      const listItems = this.similarClientIndexArray.map((index) =>
        this.similarClientListItems.push(<li key={index.toString()}>{this.props.clientID}</li>)
      );
  }

  getSimilarClientIndexArray() {
    let result = [];
    for (let i=0; i<this.gameState.similarClients.length; i++) {
      result.push(i);
    }

    return result;
  }
}

export default RoomScreen;
