import React from 'react';
import Location from './Location.js';

class Game extends React.Component {

  constructor(props) {

    super(props);

    let locationHelper = new Location();

    this.state = {
      location: locationHelper.getStartingLocation()
    };

    console.log("Here is the starting location:" + this.state.location);

  }

  updateLocation(e) {

    console.log("EVENT:" + e.relatedTarget);
  }

  render() {

    return (
        <div>
          <button onClick={this.updateLocation}> Trigger Event </button>
        </div>
    );
  }

}

export default Game;
