const http = require('http');

class Client {


  // This function sends a generic HTTP get response to my local test server
  sendGETRequest() {

    const options = {
      hostname: 'webcode.me',
      port: 80,
      path: '/',
      method: 'GET'
    };

    const clientGETReq = http.request(options, function(res) {

      console.log(`STATUS CODE: ${res.statusCode}`);

      clientGETReq.on('data', function(data) {
        process.stdout.write(data);
      });

    });

    clientGETReq.on('error', function(err) {
      console.log('ERROR:' + err);
    });

    clientGETReq.end();

  }

  sendPOSTRequest() {

  }
}

export default Client;
