import React from 'react';
import Location from './Location.js';



class Game extends React.Component {

  constructor(props) {

    super(props);

    let locationHelper = new Location();

    this.state = {
      location: locationHelper.getStartingLocation()
    };

    console.log("Here is the starting location:" + this.state.location);

  }

  //Update the current location in the state when the button is clicked
  updateLocation(e) {

    //e.target.id == {NavN, NavE, NavS, NavW} - gets converted to an int in Location class
    this.setState({
      location: locationHelper.getNextLocation(e.target.id)
    });
  }

  render() {

    return (
        <div>
          <button onClick={this.updateLocation} id="NavN"> Trigger Event </button>
          <button onClick={this.updateLocation} id="NavE"> Trigger Event </button>
          <button onClick={this.updateLocation} id="NavS"> Trigger Event </button>
          <button onClick={this.updateLocation} id="NavW"> Trigger Event </button>
          {console.log("CURRENT LOCATION:" + this.state.location)}
        </div>
    );
  }

}

export default Game;
