import React from 'react';
import Location from './Location.js';

class Game extends React.Component {

  constructor(props) {

    super(props);

    locationHelper = new Location();

    this.state = {
      location: locationHelper.getStartingLocation()
    };

    print("Here is the starting location:" + this.state.location);

  }

}

export default Game;
