import React from 'react';

class RoomScreen extends React.Component {

  constructor(props) {
    super(props);
    this.similarClientListItems = [];
  }

  render() {

    this.renderSimilarClients();

    return (
      <div>
        <h1>Room Code: {this.props.gameState.roomID}</h1>
        <h2>Players:</h2>
        <ul>
          <li>You: {this.props.gameState.clientID}</li>
          {this.similarClientListItems}
        </ul>
      </div>
    )
  }

  renderSimilarClients() {

      if (this.props.gameState.similarClients.length > 0) {
        for (let clientID of this.props.gameState.similarClients) {
            this.similarClientListItems.push(<li key={clientID}>{this.props.clientID}</li>);
        }
      }

      console.log(`SimilarClientsListItems: ${this.similarClientListItems}`);
  }
}

export default RoomScreen;
